<template>
  <BoxTwo class="box-container" :title="title">
    <div ref="bar" class="graphicsBox"></div>
  </BoxTwo>
</template>

<script>
export default {
  name: "rightTwo",
  props: ["title"],
  data() {
    return {
      //
      timer: null,
      //
      tableData: [],
      //
      chartData: [],
      //
      year: null
    };
  },
  watch: {
    chartData(val) {
      this.chartData = val;
      this.setBar();
    }
  },
  created() {
    const date = new Date();
    this.year = date.getFullYear();
    this.getList(2, this.year);
  },
  mounted() {
    this.setBar();
    this.timer = setInterval(() => {
      this.getList(2, this.year);
    }, 300000);
  },
  methods: {
    getList(type, year) {
      this.$axios
        .post("/apis/visualizing/getPlungeIntoAccountStatistics", {
          type: type,
          year: year
        })
        .then(rs => {
          if (rs.data.statusCode != "200") {
            return;
          }
          this.tableData = rs.data.result;
          this.tableData = this.delSameObjValue(
            this.tableData,
            "amount",
            ["day"],
            ["amount"]
          );
          this.chartData = this.tableData.map(item => {
            if (type == 1 || type == 2) {
              return {
                label: item.day,
                value: (item.amount / 10).toFixed(1)
              };
            } else if (type == 3) {
              return {
                label: "第" + parseInt(item.day) + "季度",
                value: (item.amount / 10).toFixed(1)
              };
            }
          });
        })
        .catch(err => {});
    },
    /* 去重累加 */
    delSameObjValue(arr, resultNum, keyName, keyValue) {
      /*  
      arr 需要处理的数组
      resultNum 最终计算结果的键名
      keyName 用于计算判断的键名 
      keyValue 用于计算结果的键名 --> 对应的键值为number类型
     */
      const warp = new Map();
      arr.forEach(i => {
        let str = keyName.map(v => i[v]).join("_");
        i[resultNum] = keyValue.reduce((p, c) => (p += i[c] * 10), 0); //keyValue值*10变为number类型
        warp.has(str)
          ? (warp.get(str)[resultNum] += i[resultNum])
          : warp.set(str, i);
      });
      return Array.from(warp).map(([, v]) => v);
    },

    /*  */
    setBar() {
      let chartDom = this.$refs.bar;
      let myChart = this.$echarts.init(chartDom);
      let option;

      const sum = this.chartData.reduce(
        (per, cur) => per + parseInt(cur.value),
        0
      );
      const border =
        "data:image/png;base64,";
      const man =
        "data:image/png;base64,";
      const woman =
        "data:image/png;base64,";
      const color = [
        "#00FFFF",
        "rgba(196,36,255,1)",
        "#115FEA",
        "#10EBE3",
        "#10A9EB",
        "#EB9C10",
        "#2E10EB",
        "#9B10EB",
        "rgb(33, 166, 161)",
        "rgb(102,113,209)",
        "rgb(230,144,78)",
        "rgb(38,185,211)",
        "rgb(82,125,12)",
        "rgb(35,70,209)"
      ];
      const data = [];
      for (let i = 0; i < this.chartData.length; i++) {
        data.push(
          {
            value: this.chartData[i].value,
            name: this.chartData[i].label,
            itemStyle: {
              normal: {
                borderWidth: 5,
                shadowBlur: 20,
                borderColor: color[i],
                shadowColor: color[i]
              }
            }
          },
          {
            value: this.chartData[0].value / 10,
            name: "",
            itemStyle: {
              normal: {
                label: {
                  show: false
                },
                labelLine: {
                  show: false
                },
                color: "rgba(0, 0, 0, 0)",
                borderColor: "rgba(0, 0, 0, 0)",
                borderWidth: 0
              }
            }
          }
        );
      }
      option = {
        color,
        graphic: {
          elements: [
            {
              type: "image",
              z: 3,
              style: {
                image: border,
                width: 200,
                height: 200
              },
              left: "center",
              top: "center",
              position: [100, 100]
            },
            {
              type: "image",
              z: 3,
              style: {
                image: man,
                width: 90,
                height: 100
              },
              left: "32%",
              top: "center"
            },
            {
              type: "image",
              z: 3,
              style: {
                image: woman,
                width: 80,
                height: 90
              },
              right: "32%",
              top: "center"
            }
          ]
        },
        tooltip: {
          show: false
        },
        toolbox: {
          show: false
        },
        series: [
          {
            name: "",
            type: "pie",
            clockWise: false,
            radius: ["60%", "62%"],
            hoverAnimation: false,
            itemStyle: {
              normal: {
                label: {
                  show: true,
                  fontSize: 13,
                  color: "#fff",
                  formatter(params) {
                    const percent = ((params.value / sum) * 100).toFixed(2);
                    return params.name
                      ? params.name + "\n" + params.value + "  " + percent + "%"
                      : "";
                  }
                },
                labelLine: {
                  width: 4,
                  length: 10,
                  length2: 20,
                  show: true,
                  color: "#00ffff"
                }
              }
            },
            data
          }
        ]
      };

      myChart.setOption(option);
      window.addEventListener("resize", function() {
        myChart.resize();
      });
      this.$on("hook:destroyed", () => {
        window.removeEventListener("resize", function() {
          myChart.resize();
        });
      });
    }
  },
  beforeDestroy() {
    if (this.timer) {
      clearInterval(this.timer);
      this.timer = null;
    }
  }
};
</script>

<style lang="less" scoped>
.box-container {
  width: 100%;
  height: 48%;
  .graphicsBox {
    width: 100%;
    height: 100%;
    margin-top: 3%;
  }
}
</style>
